<script>
import { Ortot } from "@/Api/apis";
export default {
  created() {},
  data() {
    return {
      ortotal:{},
    };
  },
  mounted() {
 
    let myChart = this.$echarts.init(document.getElementById("myChart"));

    Ortot().then((res) => {
      
      this.ortotal=res.data

      let option = {
        title: {
          text: "数据统计",
        },
        tooltip: {
          trigger: "axis",
        },
        legend: {
          data: [
            "总订单",
            "订单数据",
            "金额数据",
            "总销售额",
            "今日订单",
            "今日销售额",
          ],
        },
        grid: {
          left: "3%",
          right: "4%",
          bottom: "3%",
          containLabel: true,
        },
        toolbox: {
          feature: {
            saveAsImage: {},
          },
        },
        xAxis: {
          type: "category",
          boundaryGap: false,
          data: res.data.xData,
        },
        yAxis: {
          type: "value",
        },
        series: [
          {
            name: "订单数据",
            type: "line",
            stack: "总量",
            data: res.data.orderData,
          },
          {
            name: "金额数据",
            type: "line",
            stack: "总量",
            data: res.data.amountData,
          },
        ],
      };
      myChart.setOption(option);
    });
  },
  methods: {
    
  },
};
</script>

